      <div class='texte'><div  class="subscription" style="margin: 10px 0pt;"><h1> Nombre des comptes  </h1></div><?php   $select_nbr = mysql_query("SELECT count(*) As nbr from compte_desactiver ");    $num_rows = mysql_num_rows($select_nbr);    $count_nbr = mysql_fetch_array($select_nbr);    $msgdiv=($num_rows<1)?'Aucune donnÃ©e.':'';?>    <?php       if($count_nbr['nbr'] < 1 ){       ?>                   <h3 style="  color: red;"> Nombre des candidats supprimer leurs compte  : <?php echo $count_nbr['nbr']; ?> </h3>     <?php } else {?>  <h3 > Nombre des candidats supprimer leurs compte   : <?php echo $count_nbr['nbr']; ?> </h3><h3>					  					  <div id='chart_div_candidats_desabonnes_de_leurs_compte' style='width: 680px; height: 300px;'></div>					 </h3>				 					 	  <!-- NOMBRES DE CANDIDATS DESABONNES de leurs compts --><script type="text/javascript">      // Load the Visualization API and the piechart package.      google.load('visualization', '1.0', {'packages':['corechart']});      // Set a callback to run when the Google Visualization API is loaded.      google.setOnLoadCallback(drawChart);      // Callback that creates and populates a data table,      // instantiates the pie chart, passes in the data and      // draws it.      function drawChart() {        // Create the data table.        var data = new google.visualization.DataTable();        data.addColumn('string', 'Topping');        data.addColumn('number', 'Slices');        data.addRows([          <?php// Create connection        $sqltype = "SELECT b.raison as raison,count(*) As nbr FROM compte_desactiver a,prm_compte_desactiver b        where a.raison = b.id_prm_compte        group by b.raison ";        $resulttype =  mysql_query($sqltype);        while($row = mysql_fetch_assoc($resulttype))          {  $type_p=$row["raison"];$nbr=$row['nbr'];         echo '["'.$type_p.' || '.$nbr.' '.'",'.$nbr.'],';              }               ?>                  ]);        // Set chart options        var options = {is3D: true,pieHole: 0.4, textStyle: {color: 'black', fontSize: 12}};        // Instantiate and draw our chart, passing in some options.        var chart = new google.visualization.PieChart(document.getElementById('chart_div_candidats_desabonnes_de_leurs_compte'));        chart.draw(data, options);      }    </script>	 <?php } ?>	</div>