      <div class='texte'><div  class="subscription" style="margin: 10px 0pt;"><h1> import des CVs </h1></div><?php   $select_nbr = mysql_query("Select count(*) As nbr from candidats c, cv_importe ci where c.candidats_id=ci.candidats_id AND c.last_connexion<>'0000-00-00' AND c.mdp<>'' ");$count_nbr = mysql_fetch_array($select_nbr); ?> <?php if($count_nbr['nbr'] < 1 ){ ?>             <h3 style="  color: red;"> Nombre des CVs importées : <?php echo $count_nbr['nbr']; ?></h3>        <?php }else { ?>  <h3> Nombre des CVs importées  <?php echo $count_nbr['nbr']; ?></h3><h3><div id='chart_div_cv' style='width: 680px; height: 300px;'></div></h3>		 		  <!-- NOMBRES DES CVs -->    <script type='text/javascript'>      google.load('visualization', '1', {'packages':['annotatedtimeline']});      google.setOnLoadCallback(drawChart);      function drawChart() {        var data = new google.visualization.DataTable();        data.addColumn('date', 'Date');        data.addColumn('number', 'Nombre de CV');        data.addRows([      <?php   $result = mysql_query("SELECT DATE_FORMAT(last_connexion,'%Y, %m-1 ,%d'),count(*) FROM candidats, cv_importe where candidats.candidats_id=cv_importe.candidats_id AND last_connexion<>0000-00-00 AND mdp<>'' group By last_connexion ");while($row = mysql_fetch_array($result))  {   echo '[new Date('.$row['0'].'),'. $row['1'].'],';    }  ?>                  ]);        var chart = new google.visualization.AnnotatedTimeLine(document.getElementById('chart_div_cv'));        chart.draw(data, {displayAnnotations: true});      }    </script>					 	 <?php } ?></div>