asic);function drawBasic() {      var data = google.visualization.arrayToDataTable([          ['offre', 'Nombre des candidatures']      <?php        $req1="SELECT *             FROM offre                        ".$q_ref_fili."            ORDER BY date_insertion DESC             LIMIT 0 , 30";                                            $reponse1 = mysql_query($req1);                 $tab;    $i=0; $ct = false;       while($donnee1=mysql_fetch_array($reponse1)  )       {       if($ct)       $i++;       if($i <30)       {         $r=$donnee1['id_offre'];        $req="SELECT  COUNT(*) AS 'nbre_offres'                                    FROM `candidature`                                     WHERE (candidature.id_offre  ='$r')                                   ";                 $reponse = mysql_query($req);               $iden=$donnee1['id_entreprise'];        $reqen="SELECT  *                                    FROM entreprise                                    WHERE id_entreprise ='$iden'                                  ";                 $reponseen = mysql_query($reqen);                   $donneen = mysql_fetch_array($reponseen);                        $donnee = mysql_fetch_array($reponse);    if($donnee['nbre_offres'] != 0)        {                  $tab[$i]=',[\''.$donnee1['Name'].'\','.$donnee['nbre_offres'].']';         echo   $tab[$i];      $ct = true;     }     {     $ct = false;     }     }                       }         ?>      ]);var options = {        title: '',        chartArea: {width: '40%',height:'100%'},        fontSize: 10,        hAxis: {          title: 'Nombre des candidatures',          minValue: 0        },        vAxis: {          title: "Offres d'emploi"        }      };      var chart = new google.visualization.BarChart(document.getElementById('visualization_de_candidatures_par_offre'));      chart.draw(data, options);}  /*      google.load("visualization", "1", {